import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Button,
  Paper,
  useTheme,
  alpha,
  Grid,
  Fade,
  Zoom,
  Slide,
  IconButton,
  Tooltip,
  CircularProgress,
} from '@mui/material';
import GoogleIcon from '@mui/icons-material/Google';
import SecurityIcon from '@mui/icons-material/Security';
import SpeedIcon from '@mui/icons-material/Speed';
import AnalyticsIcon from '@mui/icons-material/Analytics';
import { useAuth } from '../contexts/AuthContext';

const Login: React.FC = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const theme = useTheme();
  const [isLoading, setIsLoading] = useState(false);
  const [hoveredFeature, setHoveredFeature] = useState<number | null>(null);

  const handleGoogleLogin = async () => {
    setIsLoading(true);
    try {
      const googleAuthUrl = `${process.env.REACT_APP_API_URL}/auth/google`;
      console.log('Initiating Google login, redirecting to:', googleAuthUrl);
      window.location.href = googleAuthUrl;
    } catch (error) {
      console.error('Login error:', error);
      setIsLoading(false);
    }
  };

  const features = [
    {
      icon: <SecurityIcon sx={{ fontSize: 40 }} />,
      title: 'Secure Authentication',
      description: 'Enterprise-grade security with Google OAuth integration'
    },
    {
      icon: <SpeedIcon sx={{ fontSize: 40 }} />,
      title: 'Fast & Efficient',
      description: 'Lightning-fast campaign delivery and real-time analytics'
    },
    {
      icon: <AnalyticsIcon sx={{ fontSize: 40 }} />,
      title: 'Smart Analytics',
      description: 'Advanced insights and performance tracking'
    }
  ];

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.primary.dark} 100%)`,
        position: 'relative',
        overflow: 'hidden',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'url(/pattern.svg)',
          opacity: 0.1,
          animation: 'float 20s linear infinite',
        },
        '@keyframes float': {
          '0%': { transform: 'translateY(0)' },
          '50%': { transform: 'translateY(-20px)' },
          '100%': { transform: 'translateY(0)' },
        },
      }}
    >
      <Container maxWidth="lg">
        <Grid container spacing={4} sx={{ minHeight: '100vh', alignItems: 'center' }}>
          {/* Left Column - Features */}
          <Grid item xs={12} md={6}>
            <Fade in timeout={1000}>
              <Box sx={{ color: 'white', pr: { md: 4 } }}>
                <Typography
                  variant="h2"
                  component="h1"
                  gutterBottom
                  sx={{
                    fontWeight: 'bold',
                    mb: 4,
                    textShadow: '2px 2px 4px rgba(0,0,0,0.2)',
                  }}
                >
                  Welcome to Mini CRM
                </Typography>
                <Typography
                  variant="h5"
                  sx={{
                    mb: 6,
                    opacity: 0.9,
                    lineHeight: 1.6,
                  }}
                >
                  Your all-in-one solution for managing marketing campaigns and customer relationships
                </Typography>

                <Grid container spacing={3}>
                  {features.map((feature, index) => (
                    <Grid item xs={12} key={index}>
                      <Zoom 
                        in 
                        timeout={1000}
                        style={{ transitionDelay: `${index * 200}ms` }}
                      >
                        <Paper
                          elevation={0}
                          sx={{
                            p: 3,
                            background: alpha(theme.palette.background.paper, 0.1),
                            backdropFilter: 'blur(10px)',
                            border: `1px solid ${alpha(theme.palette.common.white, 0.1)}`,
                            borderRadius: 2,
                            transition: 'all 0.3s ease',
                            transform: hoveredFeature === index ? 'translateY(-5px)' : 'none',
                            cursor: 'pointer',
                            '&:hover': {
                              background: alpha(theme.palette.background.paper, 0.2),
                              transform: 'translateY(-5px)',
                            },
                          }}
                          onMouseEnter={() => setHoveredFeature(index)}
                          onMouseLeave={() => setHoveredFeature(null)}
                        >
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            <Box
                              sx={{
                                p: 1,
                                borderRadius: 2,
                                background: alpha(theme.palette.common.white, 0.1),
                                color: 'white',
                              }}
                            >
                              {feature.icon}
                            </Box>
                            <Box>
                              <Typography variant="h6" sx={{ color: 'white', mb: 0.5 }}>
                                {feature.title}
                              </Typography>
                              <Typography variant="body2" sx={{ color: alpha(theme.palette.common.white, 0.7) }}>
                                {feature.description}
                              </Typography>
                            </Box>
                          </Box>
                        </Paper>
                      </Zoom>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            </Fade>
          </Grid>

          {/* Right Column - Login */}
          <Grid item xs={12} md={6}>
            <Slide direction="left" in timeout={1000}>
              <Box
                sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  minHeight: '100vh',
                  py: 4,
                }}
              >
                <Paper
                  elevation={24}
                  sx={{
                    p: 4,
                    width: '100%',
                    maxWidth: 400,
                    background: alpha(theme.palette.background.paper, 0.9),
                    backdropFilter: 'blur(10px)',
                    borderRadius: 4,
                    border: `1px solid ${alpha(theme.palette.divider, 0.1)}`,
                  }}
                >
                  <Box sx={{ textAlign: 'center', mb: 4 }}>
                    <Typography
                      variant="h4"
                      component="h2"
                      gutterBottom
                      sx={{ fontWeight: 'bold', color: theme.palette.primary.main }}
                    >
                      Get Started
                    </Typography>
                    <Typography variant="body1" color="text.secondary">
                      Sign in to access your dashboard
                    </Typography>
                  </Box>

                  <Box sx={{ mb: 3 }}>
                    <Button
                      fullWidth
                      variant="contained"
                      size="large"
                      onClick={handleGoogleLogin}
                      disabled={isLoading}
                      startIcon={isLoading ? <CircularProgress size={20} color="inherit" /> : <GoogleIcon />}
                      sx={{
                        py: 1.5,
                        borderRadius: 2,
                        textTransform: 'none',
                        fontSize: '1.1rem',
                        background: theme.palette.common.white,
                        color: theme.palette.text.primary,
                        boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
                        '&:hover': {
                          background: alpha(theme.palette.common.white, 0.9),
                          transform: 'translateY(-2px)',
                          boxShadow: '0 6px 16px rgba(0,0,0,0.15)',
                        },
                        transition: 'all 0.3s ease',
                      }}
                    >
                      {isLoading ? 'Signing in...' : 'Continue with Google'}
                    </Button>
                  </Box>

                  <Box sx={{ mt: 4, textAlign: 'center' }}>
                    <Typography variant="body2" color="text.secondary">
                      By continuing, you agree to our{' '}
                      <Typography
                        component="span"
                        variant="body2"
                        sx={{
                          color: theme.palette.primary.main,
                          cursor: 'pointer',
                          '&:hover': { textDecoration: 'underline' },
                        }}
                      >
                        Terms of Service
                      </Typography>
                    </Typography>
                  </Box>
                </Paper>
              </Box>
            </Slide>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Login;
