import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
  Container,
  Box,
  Typography,
  Button,
  Paper,
  useTheme,
} from '@mui/material';
import GoogleIcon from '@mui/icons-material/Google';
import CampaignIcon from '@mui/icons-material/Campaign';
import config from '../config';

const Login: React.FC = () => {
  const { login, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const theme = useTheme();

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/');
    }
  }, [isAuthenticated, navigate]);

  const handleLogin = () => {
    window.location.href = `${config.API_URL}/api/auth/google`;
  };

  return (
    <Container component="main" maxWidth="xs">
      <Box
        sx={{
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          background: `linear-gradient(45deg, ${theme.palette.primary.main} 30%, ${theme.palette.primary.dark} 90%)`,
          padding: 2,
        }}
      >
        <Paper
          elevation={6}
          sx={{
            padding: 4,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            width: '100%',
            borderRadius: 2,
            background: 'rgba(255, 255, 255, 0.95)',
          }}
        >
          <CampaignIcon sx={{ fontSize: 60, color: theme.palette.primary.main, mb: 2 }} />
          <Typography component="h1" variant="h4" sx={{ mb: 1, fontWeight: 'bold', color: theme.palette.primary.main }}>
            Mini CRM
          </Typography>
          <Typography variant="subtitle1" sx={{ mb: 4, color: 'text.secondary', textAlign: 'center' }}>
            Your all-in-one marketing campaign management solution
          </Typography>
          <Button
            variant="contained"
            startIcon={<GoogleIcon />}
            onClick={handleLogin}
            sx={{
              mt: 2,
              py: 1.5,
              px: 4,
              borderRadius: 2,
              textTransform: 'none',
              fontSize: '1.1rem',
              boxShadow: 3,
              '&:hover': {
                boxShadow: 6,
              },
            }}
            fullWidth
          >
            Sign in with Google
          </Button>
          <Typography variant="body2" sx={{ mt: 4, color: 'text.secondary', textAlign: 'center' }}>
            Secure, fast, and easy to use
          </Typography>
        </Paper>
      </Box>
    </Container>
  );
};

export default Login;
