import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
  Container,
  Box,
  Typography,
  Button,
  Paper,
  useTheme,
  Grid,
  Fade,
  Zoom,
  Slide,
  alpha,
  IconButton,
  Tooltip,
  CircularProgress,
  keyframes
} from '@mui/material';
import GoogleIcon from '@mui/icons-material/Google';
import CampaignIcon from '@mui/icons-material/Campaign';
import SecurityIcon from '@mui/icons-material/Security';
import SpeedIcon from '@mui/icons-material/Speed';
import AnalyticsIcon from '@mui/icons-material/Analytics';
import config from '../config';

// Define custom animations using keyframes
const glow = keyframes`
  0% { box-shadow: 0 0 5px ${alpha('#fff', 0.2)}; }
  50% { box-shadow: 0 0 20px ${alpha('#fff', 0.6)}; }
  100% { box-shadow: 0 0 5px ${alpha('#fff', 0.2)}; }
`;

const floatAnimation = keyframes`
  0% { transform: translateY(0) rotate(0deg); }
  25% { transform: translateY(-5px) rotate(1deg); }
  50% { transform: translateY(0) rotate(-1deg); }
  75% { transform: translateY(5px) rotate(1deg); }
  100% { transform: translateY(0) rotate(0deg); }
`;

const Login: React.FC = () => {
  const { login, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const theme = useTheme();
  const [isLoading, setIsLoading] = useState(false);
  const [hoveredFeature, setHoveredFeature] = useState<number | null>(null);
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/');
    }
  }, [isAuthenticated, navigate]);

  // Effect for parallax background
  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleGoogleLogin = async () => {
    setIsLoading(true);
    try {
      const googleAuthUrl = `${config.API_URL}/api/auth/google`;
      console.log('Initiating Google login, redirecting to:', googleAuthUrl); // Debug log
      window.location.href = googleAuthUrl;
    } catch (error) {
      console.error('Login error:', error);
      setIsLoading(false);
    }
  };

  const features = [
    {
      icon: <SecurityIcon sx={{ fontSize: 40 }} />,
      title: 'Secure Authentication',
      description: 'Enterprise-grade security with Google OAuth integration'
    },
    {
      icon: <SpeedIcon sx={{ fontSize: 40 }} />,
      title: 'Fast & Efficient',
      description: 'Lightning-fast campaign delivery and real-time analytics'
    },
    {
      icon: <AnalyticsIcon sx={{ fontSize: 40 }} />,
      title: 'Smart Analytics',
      description: 'Advanced insights and performance tracking'
    }
  ];

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.primary.dark} 100%)`,
        position: 'relative',
        overflowX: 'hidden', // Prevent horizontal scroll
        overflowY: 'auto', // Allow vertical scroll
        // Subtle parallax background effect
        '&::before': {
          content: '""',
          position: 'fixed', // Use fixed to keep it in viewport
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'url(/pattern.svg)',
          opacity: 0.15, // Increased opacity slightly
          zIndex: -1, // Ensure it's behind content
          transform: `translateY(-${scrollY * 0.1}px)`, // Parallax effect
          transition: 'transform 0.1s ease-out', // Smooth transition for parallax
        },
      }}
    >
      <Container maxWidth="lg">
        <Grid container spacing={4} sx={{ minHeight: '100vh', alignItems: 'center', py: 8 }}> {/* Added vertical padding */}
          {/* Left Column - Features */}
          <Grid item xs={12} md={6}>
            <Fade in timeout={1500}> {/* Increased timeout */}
              <Box sx={{ color: 'white', pr: { md: 4 }, textAlign: { xs: 'center', md: 'left' } }}> {/* Centered text on mobile */}
                <Typography
                  variant="h2"
                  component="h1"
                  gutterBottom
                  sx={{
                    fontWeight: 'bold',
                    mb: 3,
                    fontSize: { xs: '2.5rem', md: '3.5rem' }, // Responsive font size
                    textShadow: '2px 2px 6px rgba(0,0,0,0.3)', // Stronger text shadow
                  }}
                >
                  Welcome to Mini CRM
                </Typography>
                <Typography
                  variant="h5"
                  sx={{
                    mb: 6,
                    opacity: 0.95, // Increased opacity
                    lineHeight: 1.7,
                    fontSize: { xs: '1.2rem', md: '1.5rem' }, // Responsive font size
                  }}
                >
                  Your all-in-one solution for managing marketing campaigns and customer relationships
                </Typography>

                <Grid container spacing={3}> {/* Consistent spacing */}
                  {features.map((feature, index) => (
                    <Grid item xs={12} key={index}>
                      <Zoom 
                        in 
                        timeout={1000}
                        style={{ transitionDelay: `${index * 200}ms` }}
                      >
                        <Paper
                          elevation={4} // Added subtle elevation
                          sx={{
                            p: 3,
                            background: alpha(theme.palette.background.paper, hoveredFeature === index ? 0.25 : 0.15), // More prominent hover effect
                            backdropFilter: 'blur(12px)', // Increased blur
                            border: `1px solid ${alpha(theme.palette.common.white, 0.2)}`, // More visible border
                            borderRadius: 3, // Slightly more rounded corners
                            transition: 'all 0.4s ease-in-out', // Smoother transition
                            transform: hoveredFeature === index ? 'translateY(-8px)' : 'none', // More pronounced lift on hover
                            cursor: 'pointer',
                            overflow: 'hidden', // Hide potential overflow from content
                            position: 'relative',
                            '&::before': { // Subtle inner glow effect
                              content: '""',
                              position: 'absolute',
                              top: '-50%', left: '-50%', right: '-50%', bottom: '-50%',
                              background: `radial-gradient(circle at center, ${alpha(theme.palette.common.white, hoveredFeature === index ? 0.15 : 0.05)} 0%, transparent 70%)`,
                              opacity: 0.7,
                              transition: 'opacity 0.4s ease-in-out',
                            },
                          }}
                          onMouseEnter={() => setHoveredFeature(index)}
                          onMouseLeave={() => setHoveredFeature(null)}
                        >
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            <Box
                              sx={{
                                p: 1.5, // Increased padding
                                borderRadius: '50%', // Circular icon background
                                background: alpha(theme.palette.common.white, 0.2), // Stronger background
                                color: 'white',
                                animation: hoveredFeature === index ? `${floatAnimation} 3s ease-in-out infinite` : 'none', // Float animation on hover
                              }}
                            >
                              {feature.icon}
                            </Box>
                            <Box>
                              <Typography variant="h6" sx={{ color: 'white', mb: 0.5, fontWeight: 'bold' }}> // Bold title
                                {feature.title}
                              </Typography>
                              <Typography variant="body2" sx={{ color: alpha(theme.palette.common.white, 0.8) }}> // More opaque description
                                {feature.description}
                              </Typography>
                            </Box>
                          </Box>
                        </Paper>
                      </Zoom>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            </Fade>
          </Grid>

          {/* Right Column - Login */}
          <Grid item xs={12} md={6}>
            <Slide direction="left" in timeout={1500}> {/* Increased timeout */}
              <Box
                sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  minHeight: { xs: 'auto', md: '100vh' }, // Adjust minHeight for mobile
                  py: { xs: 6, md: 4 }, // Added vertical padding
                }}
              >
                <Paper
                  elevation={24}
                  sx={{
                    p: 4, // Increased padding
                    width: '100%',
                    maxWidth: 450, // Increased max width slightly
                    background: alpha(theme.palette.background.paper, 0.95), // Less transparent
                    backdropFilter: 'blur(15px)', // Increased blur
                    borderRadius: 4, // More rounded corners
                    border: `1px solid ${alpha(theme.palette.divider, 0.2)}`, // More visible border
                    boxShadow: `0 8px 40px ${alpha(theme.palette.primary.main, 0.3)}`, // More prominent and colored shadow
                    position: 'relative',
                    overflow: 'hidden',
                    animation: `${glow} 5s ease-in-out infinite`, // Added glow animation to the card
                  }}
                >
                  <Box sx={{ textAlign: 'center', mb: 4 }}>
                    <Typography
                      variant="h4"
                      component="h2"
                      gutterBottom
                      sx={{ fontWeight: 'bold', color: theme.palette.primary.dark, textShadow: '1px 1px 3px rgba(0,0,0,0.1)' }} // Darker color and shadow
                    >
                      Get Started
                    </Typography>
                    <Typography variant="body1" color="text.secondary">
                      Sign in to access your dashboard
                    </Typography>
                  </Box>

                  <Box sx={{ mb: 3 }}> {/* Added margin bottom */}
                    <Button
                      fullWidth
                      variant="contained"
                      size="large"
                      onClick={handleGoogleLogin}
                      disabled={isLoading}
                      startIcon={isLoading ? <CircularProgress size={20} color="inherit" /> : <GoogleIcon />}
                      sx={{
                        py: 1.8, // Increased padding
                        borderRadius: 2,
                        textTransform: 'none',
                        fontSize: '1.1rem',
                        background: theme.palette.common.white,
                        color: theme.palette.text.primary,
                        boxShadow: '0 6px 16px rgba(0,0,0,0.2)', // Stronger button shadow
                        '&:hover': {
                          background: alpha(theme.palette.common.white, 0.95), // Less transparency on hover
                          transform: 'translateY(-3px)', // More pronounced lift
                          boxShadow: '0 8px 20px rgba(0,0,0,0.25)', // Stronger shadow on hover
                        },
                        transition: 'all 0.3s ease-in-out', // Smoother transition
                      }}
                    >
                      {isLoading ? 'Signing in...' : 'Continue with Google'}
                    </Button>
                  </Box>

                  <Box sx={{ mt: 4, textAlign: 'center' }}>
                    <Typography variant="body2" color="text.secondary">
                      By continuing, you agree to our{' '}
                      <Typography
                        component="span"
                        variant="body2"
                        sx={{
                          color: theme.palette.primary.main,
                          cursor: 'pointer',
                          fontWeight: 'bold', // Bold terms link
                          '&:hover': { textDecoration: 'underline' },
                          transition: 'color 0.2s ease-in-out',
                        }}
                      >
                        Terms of Service
                      </Typography>
                    </Typography>
                  </Box>
                </Paper>
              </Box>
            </Slide>
          </Grid>
        </Grid>
      </Container>
